import React, { useState } from 'react';
// import { Link } from "react-router-dom";
import './login.css';

const Login = () => {
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [phoneNumberError, setPhoneNumberError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validatePhoneNumber(phoneNumber)) {
      setPhoneNumberError('Please enter a valid phone number.');
      return;
    }

    if (!validateEmail()) {
      return;
    }

    console.log('Form submitted');
  };

  const validatePhoneNumber = (phoneNumber) => {
    const digitsOnly = phoneNumber.replace(/\D/g, '');

    if (digitsOnly.length !== 10) {
      return false;
    }

    return true;
  };

  const handlePhoneNumberChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const validateEmail = () => {
    // Implement your email validation logic here
  };

  function con() {
    alert('Logged In Successfully');
  }

  return (
    <div>
      <div className='login'>
        <div className='image'>
          <img src='byjus.png' alt='logo' width='150' height='100'></img>
        </div>
        <p>
          <b>Let's Get Started</b>
          <br />
          Enter your mobile number to sign up/sign in to your BYJU'S account
        </p>
        <br />
        <div className='form'>
          <div className='name'>
            <label>Name:</label>
            <br />
            <input type='text' />
          </div>
          <br />
          <div className='form-phno'>
            <label htmlFor='phoneNumber'>Phone Number:</label>
            <br />
            <input
              type='tel'
              id='phoneNumber'
              name='phoneNumber'
              className={`form-control ${phoneNumberError && 'error'}`}
              value={phoneNumber}
              onChange={handlePhoneNumberChange}
            />
            {phoneNumberError && <div className='error-message'>{phoneNumberError}</div>}
          </div>
        </div>
        <div className='btn'>
          <button onClick={con}>Continue</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
